import express from 'express';
import { validateSignup, validateLogin, validateUserUpdate } from '../middleware/user.validation';
import { signup, login, getAllUsers, getSpecificUser, updateSpecificUser } from '../controller/user.controller';


const usersRouter = express.Router();

usersRouter.post('/auth/signup', [validateSignup], signup);

usersRouter.post('/auth/signin', [validateLogin], login);

usersRouter.get('/auth/users', getAllUsers);

usersRouter.get('/auth/user/:id', getSpecificUser);

usersRouter.patch('/auth/user/:id', [validateUserUpdate], updateSpecificUser);

export default usersRouter;

import { signupInput, loginInput, userUpdateInput } from '../helper/user.input';


const validateSignup = (req, res, next) => {
  const { error } = signupInput(req);
  if (error) return res.status(400).json({ status: res.statusCode, error: error.details[0].message });

  next();
};

const validateLogin = (req, res, next) => {
  const { error } = loginInput(req);
  if (error) return res.status(400).json({ status: res.statusCode, error: error.details[0].message });

  next();
};

const validateUserUpdate = (req, res, next) => {
  const { error } = userUpdateInput(req);
  if (error) return res.status(400).json({ status: res.statusCode, error: error.details[0].message });

  next();
};



export { validateSignup, validateLogin, validateUserUpdate };

import Joi from '@hapi/joi';

const signupInput = (req) => {
  const schema = Joi.object().keys({
    name: Joi.string().required().min(2).max(50),
    email: Joi.string().required().email(),
    password: Joi.string().required().min(6).max(15),
    role: Joi.string().optional()
  });

  return schema.validate(req.body);
};

const loginInput = (req) => {
  const schema = Joi.object().keys({
    email: Joi.string().required().email(),
    password: Joi.string().required().min(6).max(15),
  });

  return schema.validate(req.body);
};

const userUpdateInput = (req) => {
  const schema = Joi.object().keys({
    name: Joi.string().optional().min(2).max(50),
    email: Joi.string().optional().email(),
    password: Joi.string().optional().min(6).max(15),
    role: Joi.string().optional()
  });

  return schema.validate(req.body);
};


export { signupInput, loginInput, userUpdateInput };

import User from '../models/user.model';
import generateToken from '../helper/generateAuthToken';
import { encryptPassword, decryptPassword } from '../helper/hashedPassword';


const users = [];

const signup = (req, res) => {


  const takenEmail = users.find((user) => user.email === req.body.email);
  if (takenEmail) return res.status(409).json({ status: 409, message: 'Email address already taken' });

  const id = users.length + 1;
  const role = req.body.role  ||  "guest";

  let { name, email, password } = req.body;

  password = encryptPassword(password);
  const user = new User(id, role, name, email, password);

  const token = generateToken(id, user.email, role);

  users.push(user);


  res.status(201).json({ status: 201, message: 'User created successfully', data: { token } });
};

const login = (req, res) => {
  const { email, password } = req.body;

  const usersFound = users.find(((userInfo) => userInfo.email === email));
  if (!usersFound) return res.status(404).send({ status: 404, message: 'No associated account with this email' });

  const isPasswordValid = decryptPassword(password, usersFound.password);
  if (!isPasswordValid) return res.status(401).json({ status: 401, message: 'Incorrect password!' });

  const token = generateToken(usersFound.id, usersFound.email);

  res.status(200).json({ status: 200, message: 'loggin successfull', data: { token } });
};


const getAllUsers = (req, res) => {

  const usersFound = users.find((user) => user );

  if (!usersFound) return res.status(404).json({ status: 404, message: 'There are no available users' });

  const allusers = users.sort((a, b) => (new Date(b.createdOn)).getTime()
  - (new Date(a.createdOn).getTime()));

  res.status(200).json({ status: 200, data: allusers });
};

const getSpecificUser = (req, res) => {
  const user = users.find((user) => user.id == req.params.id );

  if (!user) return res.status(404).json({ status: 404, message: 'No user found' });

  return res.status(200).json({ status: 200, data: user });
};

const updateSpecificUser = (req, res) => {
  const user = users.find((user) => user.id == req.params.id );

  if (!user) return res.status(404).json({ status: 404, message: 'No user found' });

  let name = req.body.name;
  let email = req.body.email;
  let password = req.body.password;
  let role = req.body.role;

  if(!name){ name = user.name; }
  if(!password){ password = user.password; }
  if(!role){ role = user.role; }
  if(!email){ email = user.email; }

  user.name = name;
  user.email = email;
  user.password = password;
  user.role = role;

  return res.status(200).json({ status: 200, message: 'user successfully updated', data: user });
};

export { signup, login, getAllUsers, getSpecificUser, updateSpecificUser, users };

class User {
    constructor(id, role, name, email, password) {
      this.id = id;
      this.role = role;
      this.name = name;
      this.email = email;
      this.password = password;
    }
  }
  
  export default User;

import jwt from 'jsonwebtoken';
import dotenv from 'dotenv'; 

dotenv.config();

const generateToken = (id, email, role) => {
  const token = jwt.sign({ Id: id, userEmail: email, role: role }, process.env.JWT_PRIVATE_KEY, { expiresIn: '24h' });

  return token;
};

export default generateToken;

import bcrypt from 'bcryptjs';

const encryptPassword = (password) => bcrypt.hashSync(password, 10);
const decryptPassword = (userPswd, hashedPswd) => bcrypt.compareSync(userPswd, hashedPswd);

export { encryptPassword, decryptPassword };

import express from 'express';
import validateBlogInput  from '../middleware/blog.validation';
import { verifyAuthToken } from '../middleware/verifyAuthToken';
import { createNewBlog, getAllBlogs, getOneBlog, updateBlog, deleteBlog } from '../controller/blog.controller';


const blogsRouter = express.Router();

blogsRouter.post('/blog', [verifyAuthToken, validateBlogInput], createNewBlog);

blogsRouter.get('/blog', getAllBlogs);

blogsRouter.get('/blog/:id', getOneBlog);

blogsRouter.patch('/blog/:id', [verifyAuthToken, validateBlogInput], updateBlog);

